# @version 0.2.11

index: uint256 
commentIndex:uint256[1000]
struct blog:
    title: String[20]
    abstractHash: String[64]
    fileHash: String[64]
    time: uint256
    sender: address
    hidden:bool
    id: uint256


struct comment:
    time: uint256
    sender: address
    content: String[100]

commentMap: HashMap[uint256, comment[1000]]
blogMap: HashMap[uint256, blog]


@external
@payable
def addBlog(title: String[20], fileHash: String[64], abstractHash: String[64]):
    self.blogMap[self.index] = blog({
        title: title,
        abstractHash: abstractHash,
        fileHash: fileHash,
        time: block.timestamp,
        sender: msg.sender,
        hidden: False,
        id: self.index})
    self.index += 1

@external
@payable
def updateBlog(_index: uint256, title: String[20], abstractHash: String[64], fileHash: String[64]):
    if self.blogMap[_index].sender == msg.sender:
        self.blogMap[_index].title = title
        self.blogMap[_index].fileHash = fileHash
        self.blogMap[_index].time = block.timestamp
        self.blogMap[_index].abstractHash = abstractHash

@external                   
@payable
def addComment(blogIndex: uint256, content: String[100]):
    self.commentMap[blogIndex][self.commentIndex[blogIndex]] = comment({
                                          time: block.timestamp,
                                          sender: msg.sender,
                                          content: content})
    self.commentIndex[blogIndex]+=1

@external                   
@payable
def deleteBlog(_index:uint256):
    if self.blogMap[_index].sender == msg.sender:
        self.blogMap[_index].hidden = True

@external
@view
def getComment(blogIndex: uint256, commentIndex: uint256) -> (uint256,address,String[100]):
    return(
        self.commentMap[blogIndex][commentIndex].time,
        self.commentMap[blogIndex][commentIndex].sender,
        self.commentMap[blogIndex][commentIndex].content
    )

@external
@view
def getCommentCount(blogIndex: uint256) -> uint256:
    return self.commentIndex[blogIndex]

@external
@view
def getBlogCount() -> uint256:
    return self.index


@external
@view
def getBlog(_index: uint256) -> ( String[20], String[64], String[64], uint256, address, uint256, uint256,bool):
    return (self.blogMap[_index].title,
            self.blogMap[_index].fileHash,
            self.blogMap[_index].abstractHash,
            self.blogMap[_index].time,
            self.blogMap[_index].sender,
            self.blogMap[_index].id,
            self.commentIndex[_index],
            self.blogMap[_index].hidden)
